INSERT INTO post(CATEGORY_ID, TITLE, AI_IMG, ACHIEVEMENT_IMG, CONTENT, ACHIEVEMENT_CONTENT, IS_ACHIEVED, IS_DISPLAY, MEMBER_ID, CREATED_DATE, MODIFIED_DATE, ACHIEVED_DATE, DEAD_LINE, CHEER_CNT, CELEBRATE_CNT) VALUES (6, '소주에 갈비살 먹기', 'https://dreamgream-image-bucket.s3.ap-northeast-2.amazonaws.com/ai/member_1/91264382-f12a-426d-ad67-1e547eb265b2_2023-08-09.png', 'https://dreamgream-image-bucket.s3.ap-northeast-2.amazonaws.com/ai/member_1/91264382-f12a-426d-ad67-1e547eb265b2_2023-08-09.png', '가족, 친구들과 함께 반나절 즐길 수 있는 네거리 골목의 집에서, 맛있는 갈비살과 함께 소주 한 잔 하며 모임의 분위기를 더해보고 싶습니다. ', '한국의 전통음식을 제대로 느끼며 맛있는 음식과 함께한 소중한 순간을 가졌습니다.', 1, 1, 1, '2020-10-10', '2021-01-01', '2021-01-01', '1년이내', 10, 1);
INSERT INTO post(CATEGORY_ID, TITLE, AI_IMG, ACHIEVEMENT_IMG, CONTENT, ACHIEVEMENT_CONTENT, IS_ACHIEVED, IS_DISPLAY, MEMBER_ID, CREATED_DATE, MODIFIED_DATE, ACHIEVED_DATE, DEAD_LINE, CHEER_CNT, CELEBRATE_CNT) VALUES (1, '파리 여행가기', 'https://dreamgream-image-bucket.s3.ap-northeast-2.amazonaws.com/ai/member_1/1991726e-0f78-47bf-b5aa-a1333e374505_2023-08-07.png', 'https://dreamgream-image-bucket.s3.ap-northeast-2.amazonaws.com/ai/member_1/1991726e-0f78-47bf-b5aa-a1333e374505_2023-08-07.png', '교양과 인문학을 쌓는 것 뿐 아니라 역사적인 유적지, 예술과 문화를 감상하며 여유로운 오랜시간을 느끼고 싶습니다.', '세계적인 도시 중 하나인 파리에서 문화와 역사, 그리고 맛있는 음식을 모두 경험했어요. ', 1, 1, 2, '2020-10-11', '2022-12-12', '2022-12-12', '5년이내', 30, 2);
INSERT INTO post(CATEGORY_ID, TITLE, AI_IMG, ACHIEVEMENT_IMG, CONTENT, ACHIEVEMENT_CONTENT, IS_ACHIEVED, IS_DISPLAY, MEMBER_ID, CREATED_DATE, MODIFIED_DATE, ACHIEVED_DATE, DEAD_LINE, CHEER_CNT, CELEBRATE_CNT) VALUES (2, '하와이에서 서핑보드 타기', 'https://dreamgream-image-bucket.s3.ap-northeast-2.amazonaws.com/ai/member_1/23a45d3d-341c-4660-b762-94b539cc622b_2023-08-08.png', 'https://dreamgream-image-bucket.s3.ap-northeast-2.amazonaws.com/ai/member_1/23a45d3d-341c-4660-b762-94b539cc622b_2023-08-08.png', '생명을 느끼며 바다 위를 헤엄친 즐거움을 느끼며 하루 종일 바다에서 느긋하도록 시간을 보내고 싶습니다. ', '인생에서 처음으로 한 번쯤은 시도하고 싶었던 것을 이뤄냈어요! 푸른 바다와 파도 위에서 물고기 같이 수영하는 느낌은 정말 감동적이었습니다.', 1, 1, 3, '2020-10-12', '2023-07-01', '2023-07-01', '10년이내', 20, 1);
