INSERT INTO post(CATEGORY_ID, TITLE, AI_IMG, ACHIEVEMENT_IMG, CONTENT, ACHIEVEMENT_CONTENT, IS_ACHIEVED, IS_DISPLAY, MEMBER_ID, CREATED_DATE, MODIFIED_DATE, ACHIEVED_DATE, DEAD_LINE, CHEER_CNT, CELEBRATE_CNT) VALUES (4, '루브루박물관 가기', 'https://dreamgream-image-bucket.s3.ap-northeast-2.amazonaws.com/ai/member_3/3bd97b1a-2c96-4624-b565-824ed0dfa174_2023-08-08.png', 'https://dreamgream-image-bucket.s3.ap-northeast-2.amazonaws.com/ai/member_3/3bd97b1a-2c96-4624-b565-824ed0dfa174_2023-08-08.png', '박물관에서 일주일동안 식겁하지 않고 볼 수 있는 전시회 보고 오고싶어! 유명 화가작품, 국내외 전시회 등 내 흥미를 유발하는 박물관에서의 문화적 시간이 내 맘에 들어!', '역사와 문화를 이해하면서 새로운 지식을 습득할 수 있으며 예술작품, 고대 유물들을 소중히 간직하게 됩니다.', 1, 1, 3, '2020-10-21', '2022-02-22', '2022-02-22', '10년이내', 1, 2);
